package easy;
import java.util.*;

public class FindTheDifference389 {
    public static char findTheDifference(String s, String t) {
        Map<Character,Integer> charMap = new HashMap<>();
        charMap.put(t.charAt(0), charMap.getOrDefault(t.charAt(0), 0)+1);
        for (int i = 0; i < s.length(); i++) {
            char sc = s.charAt(i);
            char tc = t.charAt(i+1);
            charMap.put(sc, charMap.getOrDefault(sc, 0)-1);
            charMap.put(tc, charMap.getOrDefault(tc, 0)+1);
        }
        for(Map.Entry<Character,Integer> entry: charMap.entrySet()){
            if(entry.getValue() != 0){
                return (char) entry.getKey();
            }
        }
        return t.charAt(s.length());
    }

    public static char findTheDifference2(String s, String t) {
        int a = t.charAt(0);
        for (int i = 0; i < s.length(); i++) {
            a -= s.charAt(i);
            a += t.charAt(i+1);
        }
        return (char) a;
    }

    public static void main(String[] args) throws Exception {
        String s = "";
        String t = "";
        // String s = "abcd";
        // String t = "bcdea";
        Date start = new Date();
        char answer = findTheDifference2(s,t);
        Date end = new Date();
        System.out.println("answer: " + answer);
        System.out.println("run time: " + (end.getTime() - start.getTime()));
    }
}
